import React, { useState } from "react";

function CreateArea()
{
    const [Note,setNote]=useState({tittle:"",content:""});
    function handleClick()
    {

    }
    function handleChange(event)
    {
        const[name,value]=event.target;
        setNote((prevNote)=>{
         return {
             ...prevNote,
             [name]:value
         };
        });
    }
    return (
        <div>
          <form>
            <input name="title" placeholder="Title" onChange={handleChange} value={Note.tittle}/>
            <textarea name="content" placeholder="Take a note..." rows="3" onChange={handleChange} value={Note.content} />
            <button onClick={handleClick}>Add</button>
          </form>
        </div>
    );
}

export default CreateArea;
